# -*- coding: utf-8 -*-

import numpy
import scipy.signal
import scipy
import sys
import peewee
import colorsys
 

def decode(a,b,c,d):
	start = ord('$')
	#print(start)
	high = (ord(a)-start) << 12
	mhigh = (ord(b)-start) << 8
	mlow = (ord(c)-start) << 4
	low = (ord(d)-start)

	return high + mhigh + mlow + low 

def preprocess(data, normal):
	output = scipy.signal.medfilt( data, 5 ) 	# median filter

	m = min(output)
	output = numpy.array([(i - m) for i in output])
	
	mx = max(output)

	print("mx")
	print(mx)

	output = numpy.array([(normal * i / mx) for i in output]) # normalized intensity from 0 to 1
	
	return output

def color_parse(signature):

	b = sum( signature[219:315] )	# max  96
	g = sum( signature[316:475] )		# 159 
	r = sum( signature[580:849] )			# 269

	b = b / 96
	g = g / 159
	r = r / 269

	h, l, s = colorsys.rgb_to_hls(r, g, b)

	if s<0.5:
		s = 0.5
	if l<0.5:
		l = 0.5

	r, g, b = colorsys.hls_to_rgb(h, l, s)
	r = r*255.0
	g = g*255.0
	b = b*255.0

	return r, g, b

def find_bias(white_bal):

	white_bal = [ decode(white_bal[4*i], white_bal[4*i+1], white_bal[4*i+2], white_bal[4*i+3] ) for i in range(29,980) ]
	white_bal = scipy.signal.medfilt(white_bal, 15)
	#white_bal = preprocess(white_bal,1000)
	
	# print("white bal")
	# print(white_bal)

	median = scipy.median(white_bal)
	white_bal = numpy.array([ (median-w)/median for w in white_bal ]) #  % deviation from median

	white_bal = [w+1 for w in white_bal]
	print("white bal norm")
	print(white_bal[:10],'...')

	return white_bal

def Process(signature, white_bal):
	signature = [ decode(signature[4*i], signature[4*i+1], signature[4*i+2], signature[4*i+3] ) for i in range(29,980) ]
	print("signature decoded")
	print(signature[:10],'...')
	
	signature = preprocess(signature, 1)

	print("signature")
	print(signature[:10],'...')

	print('sig stats...', len(signature), type(signature), 'white_bal stats..', len(white_bal),type(white_bal))
	signature = scipy.multiply(signature, white_bal)

	print("signature * wb")
	print(signature[:10],'...')

	return signature




